CREATE OR REPLACE FUNCTION search_vital_signs_review_pck.obter_desc_sinal_vital ( nm_campo_p text, nm_tabela_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w          varchar(255);

BEGIN
if nm_tabela_p = 'ATENDIMENTO_SINAL_VITAL' then
    select     substr(obter_desc_expressao(coalesce(a.CD_EXP_LABEL, a.CD_EXP_LABEL_GRID)), 1,255)
    into STRICT     ds_retorno_w
    from     TABELA_VISAO_ATRIBUTO a
    where     a.nr_sequencia = 101134
    and     a.NM_ATRIBUTO = nm_campo_p;
	
elsif nm_tabela_p = 'ATENDIMENTO_MONIT_RESP' then
    select     substr(obter_desc_expressao(coalesce(a.CD_EXP_LABEL, a.CD_EXP_LABEL_GRID)), 1,255)
    into STRICT     ds_retorno_w
    from     TABELA_VISAO_ATRIBUTO a
    where     a.nr_sequencia = 68825
    and     a.NM_ATRIBUTO = nm_campo_p;

elsif nm_tabela_p = 'ATEND_MONIT_HEMOD' then
    select     substr(obter_desc_expressao(coalesce(a.CD_EXP_LABEL, a.CD_EXP_LABEL_GRID)), 1,255)
    into STRICT     ds_retorno_w
    from     TABELA_VISAO_ATRIBUTO a
    where     a.nr_sequencia = 33258
    and     a.NM_ATRIBUTO = nm_campo_p;
	
elsif nm_tabela_p = 'ATEND_ANAL_BIOQ_PORT' then
    select     substr(obter_desc_expressao(coalesce(a.CD_EXP_LABEL, a.CD_EXP_LABEL_GRID)), 1,255)
    into STRICT     ds_retorno_w
    from     TABELA_VISAO_ATRIBUTO a
    where     a.nr_sequencia = 68824
    and     a.NM_ATRIBUTO = nm_campo_p;	
elsif nm_tabela_p = 'ATEND_AVAL_ANALGESIA' then
    select     substr(obter_desc_expressao(coalesce(a.CD_EXP_LABEL, a.CD_EXP_LABEL_GRID)), 1,255)
    into STRICT     ds_retorno_w
    from     TABELA_VISAO_ATRIBUTO a
    where     a.nr_sequencia = 29577
    and     a.NM_ATRIBUTO = nm_campo_p;	
end if;

return ds_retorno_w;

end;


