CREATE OR REPLACE FUNCTION shift_integracao_pck.obter_se_convenio_integra ( cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE

 
	ds_retorno_w varchar(1);

	
BEGIN 
 
		select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_retorno_w 
		from	convenio 
		where	(ie_matricula_integ IS NOT NULL AND ie_matricula_integ::text <> '') 
		and		cd_convenio = cd_convenio_p 
		and		ie_situacao = 'A';
 
		return ds_retorno_w;
 
	END;

$body$
