CREATE OR REPLACE FUNCTION shift_integracao_pck.obter_se_equipamento_integra ( cd_exame_equip_p text, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE

 
	ds_retorno_w varchar(1);

	
BEGIN 
 
		select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_retorno_w 
		from	lab_exame_equip a, 
				exame_laboratorio b 
		where	a.cd_exame_equip = cd_exame_equip_p 
		and		a.nr_seq_exame = b.nr_seq_exame 
		and		a.nr_seq_material = nr_seq_material_p 
		and		a.cd_equipamento in (SELECT max(cd_equipamento) 
								from equipamento_lab 
								where ds_sigla = 'SHIFTWS') 
		and		b.cd_cgc_externo in (select 	z.cd_cgc 
									from pessoa_juridica_estab z 
									where (z.nr_seq_xml_lab_envio = 101426 or z.nr_seq_xml_lab_ret = 101427));
 
		return ds_retorno_w;
 
	END;

