CREATE OR REPLACE PROCEDURE sla_dashboard_pck.atualizar_valor_insert_coin ( nr_seq_sla_regra_p bigint, nr_seq_ordem_serv_p bigint, dt_ordem_servico_p timestamp, cd_estabelecimento_p man_localizacao.cd_estabelecimento%type) AS $body$
DECLARE


qt_min_inicio_regra_w		man_sla_regra.qt_min_inicio%type;
qt_min_termino_regra_w		man_sla_regra.qt_min_termino%type;
ie_prioridade_regra_w		man_sla_regra.ie_prioridade%type;
ie_tempo_regra_w		man_sla_regra.ie_tempo%type;
ie_tempo_termino_regra_w	man_sla_regra.ie_tempo_termino%type;
dt_inicio_prev_w		timestamp;
dt_termino_prev_w		timestamp;
dt_inicio_sla_w			man_ordem_serv_sla.dt_inicio_sla%type;
nr_seq_localizacao_w		bigint;

BEGIN

	select 	max(nr_seq_localizacao)
	into STRICT	nr_seq_localizacao_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_ordem_serv_p;

	select	max(b.qt_min_inicio),
		max(b.qt_min_termino),
		max(b.ie_tempo)
	into STRICT	qt_min_inicio_regra_w,
		qt_min_termino_regra_w,
		ie_tempo_regra_w
	from	man_sla_regra b
	where	nr_sequencia = nr_seq_sla_regra_p;

	if (ie_tempo_regra_w = 'COR') then
		dt_inicio_prev_w	:= dt_ordem_servico_p + (qt_min_inicio_regra_w / 1440);
		dt_termino_prev_w	:= dt_ordem_servico_p + (qt_min_termino_regra_w / 1440);
	else
		dt_inicio_prev_w	:= sla_dashboard_pck.man_obter_hor_com_tz(cd_estabelecimento_p, dt_ordem_servico_p, qt_min_inicio_regra_w, nr_seq_localizacao_w);
		dt_termino_prev_w	:= sla_dashboard_pck.man_obter_hor_com_tz(cd_estabelecimento_p, dt_ordem_servico_p, qt_min_termino_regra_w, nr_seq_localizacao_w);
	end if;
	
	update	man_ordem_serv_sla
	set	qt_desvio = 0,
		dt_inicio_prev = dt_inicio_prev_w,
		dt_termino_prev = dt_termino_prev_w,
		dt_inicio_sla	= dt_ordem_servico_p
	where	nr_seq_ordem = nr_seq_ordem_serv_p;
	
	commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
