SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE sla_dashboard_pck.atualizar_valor_serv_sla ( nr_seq_sla_regra_p bigint, nr_seq_ordem_serv_p bigint, dt_ordem_servico_p timestamp, cd_estabelecimento_p man_localizacao.cd_estabelecimento%type, nr_seq_localizacao_p bigint) AS $body$
DECLARE


qt_min_inicio_regra_w		man_sla_regra.qt_min_inicio%type;
qt_min_termino_regra_w		man_sla_regra.qt_min_termino%type;
ie_prioridade_regra_w		man_sla_regra.ie_prioridade%type;
ie_tempo_regra_w		man_sla_regra.ie_tempo%type;
ie_tempo_termino_regra_w	man_sla_regra.ie_tempo_termino%type;
dt_inicio_prev_w		timestamp;
dt_termino_prev_w		timestamp;
nr_seq_localizacao_w		bigint;
nr_seq_sla_w			man_sla_regra.nr_seq_sla%type;
ie_tipo_sla_termino_w		man_sla_regra.ie_tipo_sla_termino%type;
ie_tipo_sla_w			man_sla_regra.ie_tipo_sla%type;

BEGIN

	select	max(b.qt_min_inicio),
		max(b.qt_min_termino),
		max(b.ie_prioridade),
		max(b.ie_tempo),
		max(b.ie_tempo_termino),
		max(b.nr_seq_sla),
		max(b.ie_tipo_sla_termino),
		max(b.ie_tipo_sla)
	into STRICT	qt_min_inicio_regra_w,
		qt_min_termino_regra_w,
		ie_prioridade_regra_w,
		ie_tempo_regra_w,
		ie_tempo_termino_regra_w,
		nr_seq_sla_w,
		ie_tipo_sla_termino_w,
		ie_tipo_sla_w
	from	man_sla_regra b
	where	nr_sequencia = nr_seq_sla_regra_p;

	if (ie_tempo_regra_w = 'COR') then
		dt_inicio_prev_w	:= dt_ordem_servico_p + (qt_min_inicio_regra_w / 1440);
		dt_termino_prev_w	:= dt_ordem_servico_p + (qt_min_termino_regra_w / 1440);
	else
		dt_inicio_prev_w	:= sla_dashboard_pck.man_obter_hor_com_tz(cd_estabelecimento_p, dt_ordem_servico_p, qt_min_inicio_regra_w, nr_seq_localizacao_p);
		dt_termino_prev_w	:= sla_dashboard_pck.man_obter_hor_com_tz(cd_estabelecimento_p, dt_ordem_servico_p, qt_min_termino_regra_w, nr_seq_localizacao_p);
	end if;

	update	man_ordem_serv_sla
	set	qt_desvio = 0,
		qt_desvio_inic = 0,
		ie_prioridade	= ie_prioridade_regra_w,
		ie_tempo = ie_tempo_regra_w,
		ie_tempo_termino = ie_tempo_termino_regra_w,
		dt_inicio_prev = dt_inicio_prev_w,
		dt_termino_prev = dt_termino_prev_w,
		qt_min_inicio = qt_min_inicio_regra_w,
		qt_min_termino   = qt_min_termino_regra_w,
		nr_seq_sla_regra = nr_seq_sla_regra_p,
		nr_seq_sla	 = nr_seq_sla_w,
		ie_tipo_sla_termino = ie_tipo_sla_termino_w,
		ie_tipo_sla	= ie_tipo_sla_w
	where	nr_seq_ordem = nr_seq_ordem_serv_p;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE sla_dashboard_pck.atualizar_valor_serv_sla ( nr_seq_sla_regra_p bigint, nr_seq_ordem_serv_p bigint, dt_ordem_servico_p timestamp, cd_estabelecimento_p man_localizacao.cd_estabelecimento%type, nr_seq_localizacao_p bigint) FROM PUBLIC;
