SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION sla_dashboard_pck.man_obter_temp_estagio_tz ( nr_sequencia_p bigint, nr_seq_ordem_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w				bigint := 0;
dt_atual_w				timestamp;
dt_atual_ww				timestamp;
dt_atualizacao_w				timestamp;
nr_seq_localizacao_w			bigint;
cd_estabelecimento_w			smallint;

/*Legenda
	D - Dias corridos
	M - Minutos corridos
	DC - Dias uteis
	MC - MInutos uteis
*/


BEGIN
/* Data de alteracao do estagio - Matheus OS 55072 em 19/04/2007 troquei dt_atualizacao_p por nr_sequencia_p*/


if (nr_seq_ordem_p > 0) and (nr_sequencia_p > 0) then
	select	dt_atualizacao
	into STRICT	dt_atualizacao_w
	from	man_ordem_serv_estagio
	where	nr_seq_ordem = nr_seq_ordem_p
	and	nr_sequencia = nr_sequencia_p;
end if;

/* Obtem dados da Ordem de Servico */



select	nr_seq_localizacao,
	coalesce(dt_fim_real,clock_timestamp())
into STRICT	nr_seq_localizacao_w,
	dt_atual_w
from	man_ordem_servico
where	nr_sequencia = nr_seq_ordem_p;

/* Estabelecimento da OS*/


begin
select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	man_localizacao
where	nr_sequencia = nr_seq_localizacao_w;
exception
when others then
	cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
end;

/* Data do proximo esagio para calcular a diferenca*/


select	min(dt_atualizacao)
into STRICT	dt_atual_ww
from	man_ordem_serv_estagio
where	nr_seq_ordem	= nr_seq_ordem_p
and	dt_atualizacao > dt_atualizacao_w;

/* Se nao tiver proximo estagio calcula pela data atual ou pela data de fim da ordem*/


if (coalesce(dt_atual_ww::text, '') = '') then
	dt_atual_ww	:= dt_atual_w;
end if;

if (ie_tipo_p = 'M') then
	qt_retorno_w			:= obter_min_entre_datas(sla_dashboard_pck.obter_datetime_tz(dt_atualizacao_w, sla_dashboard_pck.obter_pais_timezone(nr_seq_localizacao_w, 'P')), sla_dashboard_pck.obter_datetime_tz(dt_atual_ww, sla_dashboard_pck.obter_pais_timezone(nr_seq_localizacao_w, 'P')),1);
elsif (ie_tipo_p = 'D') then
	qt_retorno_w			:= obter_dias_entre_datas(sla_dashboard_pck.obter_datetime_tz(dt_atualizacao_w, sla_dashboard_pck.obter_pais_timezone(nr_seq_localizacao_w, 'P')) ,sla_dashboard_pck.obter_datetime_tz(dt_atual_ww, sla_dashboard_pck.obter_pais_timezone(nr_seq_localizacao_w, 'P')));
elsif (ie_tipo_p = 'MC') then
	qt_retorno_w			:= sla_dashboard_pck.obter_tempo_comercial_loc_tz(dt_atualizacao_w,dt_atual_ww,'MC', nr_seq_localizacao_w);
elsif (ie_tipo_p = 'DC') then
	qt_retorno_w			:= sla_dashboard_pck.obter_tempo_comercial_loc_tz(dt_atualizacao_w,dt_atual_ww,'DC', nr_seq_localizacao_w);
end if;

return	qt_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION sla_dashboard_pck.man_obter_temp_estagio_tz ( nr_sequencia_p bigint, nr_seq_ordem_p bigint, ie_tipo_p text) FROM PUBLIC;
