CREATE OR REPLACE FUNCTION sla_dashboard_pck.obter_data_fim_solucao ( nr_seq_ordem_p bigint, dt_inicio_sla_p timestamp, dt_ordem_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_final_w 	timestamp;
qt_evento_13_w	integer;


BEGIN
	select 	count(1)
	into STRICT	qt_evento_13_w
	from	man_sla_evento
	where 	nr_seq_ordem = nr_seq_ordem_p
	and	ie_tipo_evento = 13;
	
	if (qt_evento_13_w > 0) then
		return clock_timestamp();
	else
		return fim_mes(coalesce(dt_inicio_sla_p, dt_ordem_p));
	end if;
	
	
END;

$body$
