CREATE OR REPLACE FUNCTION sla_dashboard_pck.obter_datetime_tz ( dt_registro_p timestamp, nm_timezone_database_p text) RETURNS timestamp AS $body$
DECLARE


qt_hora_w	smallint;
dt_calculo_w	timestamp;


BEGIN

if (dt_registro_p IS NOT NULL AND dt_registro_p::text <> '') and (coalesce(nm_timezone_database_p,'X') <> 'X') then
	begin
	select	sla_dashboard_pck.obter_pais_timezone(nm_timezone_database_p,'H')
	into STRICT	qt_hora_w
	;
	
	dt_calculo_w := dt_registro_p + (qt_hora_w/24);
	
	end;
end if;

return dt_calculo_w;

END;

