SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION sla_dashboard_pck.obter_detalhe_sla ( ie_tipo_sla_p bigint, ie_tipo_sla_termino_p bigint) RETURNS varchar AS $body$
DECLARE

				
ie_retorno_w	varchar(10);

BEGIN
		/* 1 Contrato com multa*/


	if (ie_tipo_sla_p       = 1)
	and (ie_tipo_sla_termino_p = 1) then 
		ie_retorno_w := 'CSFP';
		/* Customer Start Finish with Penalty'*/


		
		/* 1 Contrato com multa*/


	elsif (ie_tipo_sla_p         = 1)
	and (ie_tipo_sla_termino_p   = 3) then
		ie_retorno_w := 'COSP';
		/* Customer Only Finish with Penalty'*/


	
		/* 2 Contrato com multa*/


	elsif (ie_tipo_sla_p        = 1)
	and (ie_tipo_sla_termino_p = 2) then
		ie_retorno_w := 'CSPFC';
		/* 2 Contrato sem multa*/


	
		/* 2 Contrato sem multa*/


	elsif (ie_tipo_sla_p        = 2)
	and (ie_tipo_sla_termino_p = 2) then
		ie_retorno_w := 'CSFNP';
		/* Customer Start Finish No Penalty'*/


		
		/*  3 Sem Contrato */


	elsif (ie_tipo_sla_p        = 3)
	and (ie_tipo_sla_termino_p = 2) then
		ie_retorno_w := 'NCSFCC';
		/* Customer Finish with contract'*/


		
		/* 2 Contrato sem multa*/


	elsif (ie_tipo_sla_p        = 2)
	and (ie_tipo_sla_termino_p = 3) then
		ie_retorno_w :=  'COSNP';
		/* Customer Only Start No Penalty'*/


		
		/*  3 Sem Contrato */


	elsif (ie_tipo_sla_p        = 3)
	and (ie_tipo_sla_termino_p = 3) then
		ie_retorno_w := 'PSFNP';
		/* Philips Start Finish No Penalty'*/


	end if;
	
	return ie_retorno_w;	
end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
