CREATE OR REPLACE FUNCTION sla_dashboard_pck.obter_pais_timezone ( nm_timezone_database_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nm_pais_w	varchar(255);
qt_hora_w	smallint;


BEGIN
if (upper(nm_timezone_database_p) like '%ARGENTINA%') then
	nm_pais_w := 'Argentina';
	qt_hora_w := 0;
	
elsif (upper(nm_timezone_database_p) like '%BERLIN%') then
	nm_pais_w := 'Alemanha';
	qt_hora_w := 5;

elsif (upper(nm_timezone_database_p) like '%AUSTRALIA%') then
	nm_pais_w := 'Australia';
	qt_hora_w := 13;
	
elsif (upper(nm_timezone_database_p) like '%JAPAN%') or (upper(nm_timezone_database_p) like '%TOKYO%') then
	nm_pais_w := 'Japao';
	qt_hora_w := 12;
	
elsif (upper(nm_timezone_database_p) like '%POLAND%') then
	nm_pais_w := 'Polonia';
	qt_hora_w := 5;

elsif (upper(nm_timezone_database_p) like '%BOGOTA%') then
	nm_pais_w := 'Colombia';
	qt_hora_w := -2;

elsif (upper(nm_timezone_database_p) like '%CANCUN%') or (upper(nm_timezone_database_p) like '%MEXICO_CITY%') or (upper(nm_timezone_database_p) like '%MONTERREY%') then
	nm_pais_w := 'Mexico';
	qt_hora_w := -2;
	
elsif (upper(nm_timezone_database_p) like '%CHIHUAHUA%') or (upper(nm_timezone_database_p) like '%MAZATLAN%') then
	nm_pais_w := 'Mexico';
	qt_hora_w := -3;
	
elsif (upper(nm_timezone_database_p) like '%HERMOSILLO%') or (upper(nm_timezone_database_p) like '%TIJUANA%') then
	nm_pais_w := 'Mexico';
	qt_hora_w := -4;
elsif (upper(nm_timezone_database_p) like '%LA_PAZ%') then
	nm_pais_w := 'Bolivia';
	qt_hora_w := -1;
end if;

if (ie_opcao_p = 'H') then
	return qt_hora_w;
else
	return	nm_pais_w;
end if;

END;

$body$
