CREATE OR REPLACE FUNCTION sla_dashboard_pck.obter_regra_sla ( cd_estabelecimento_p bigint, nr_sequencia_p bigint, nm_usuario_p text, ie_prioridade_p text, ie_classificacao_p text, nr_seq_localizacao_p bigint, nr_seq_grupo_trab_p bigint, dt_ordem_servico_p timestamp, dt_inicio_p timestamp, nr_seq_estagio_p bigint, nr_seq_equipamento_p bigint, nr_seq_classif_p bigint, ie_parado_p text, ie_ambiente_p text) RETURNS bigint AS $body$
DECLARE


qt_min_inicio_w			    bigint;
qt_min_termino_w		    bigint;
nr_seq_sla_w			    bigint;
nr_seq_sla_regra_w	    	    bigint;
nr_sequencia_w			    bigint;
dt_inicio_prev_w		    timestamp;
dt_termino_prev_w		    timestamp;
qt_reg_w		            bigint;
ie_tempo_w			    varchar(15);
nr_seq_estagio_w		    bigint;
nr_seq_tipo_equip_w	            bigint;
nr_seq_tipo_equip_ww		    bigint;
nr_seq_equipamento_w		    bigint;
ie_tipo_sla_w		    	    man_sla_regra.ie_tipo_sla%type;
ie_utiliza_sla_w		    varchar(255) := obter_valor_param_usuario(299,57,wheb_usuario_pck.get_cd_perfil,coalesce(nm_usuario_p,wheb_usuario_pck.get_nm_usuario),wheb_usuario_pck.get_cd_estabelecimento);
ie_tempo_termino_w		    man_sla_regra.ie_tempo_termino%type;
ie_tipo_sla_termino_w		    man_sla_regra.ie_tipo_sla_termino%type;

C01 CURSOR FOR
SELECT	b.nr_seq_sla,
	b.nr_sequencia
FROM man_sla_regra b, man_sla a
LEFT OUTER JOIN man_sla_loc c ON (a.nr_sequencia = c.nr_seq_sla)
WHERE a.nr_sequencia					= b.nr_seq_sla  and a.dt_vigencia					<= clock_timestamp() and dt_ordem_servico_p between a.dt_vigencia and coalesce(a.dt_fim_vigencia, fim_ano(clock_timestamp())) and coalesce(c.nr_seq_local,nr_seq_localizacao_p)	= nr_seq_localizacao_p and coalesce(a.nr_seq_grupo_trab, coalesce(nr_seq_grupo_trab_p,0))	= coalesce(nr_seq_grupo_trab_p,0) and b.ie_classificacao					= ie_classificacao_p and coalesce(ie_prioridade,ie_prioridade_p)			= ie_prioridade_p and coalesce(b.ie_ambiente, coalesce(ie_ambiente_p,0))		= coalesce(ie_ambiente_p,0) and coalesce(nr_seq_estagio, coalesce(nr_seq_estagio_p,0))	 	= coalesce(nr_seq_estagio_p,0) and coalesce(nr_seq_classif, coalesce(nr_seq_classif_p,0))	 	= coalesce(nr_seq_classif_p,0) and coalesce(nr_seq_equipamento, coalesce(nr_seq_equipamento_p,0)) 	= coalesce(nr_seq_equipamento_p,0) and coalesce(nr_seq_tipo_equipamento, coalesce(nr_seq_tipo_equip_w,0))	= coalesce(nr_seq_tipo_equip_w,0) and coalesce(b.ie_parado, coalesce(ie_parado_p,'X'))			= coalesce(ie_parado_p,'X') and a.ie_situacao				= 'A' and (c.nr_seq_local IS NOT NULL AND c.nr_seq_local::text <> '') order by	coalesce(ie_prioridade,' '),
	b.nr_seq_sla;

BEGIN

select	count(*)
into STRICT	qt_reg_w
from	man_ordem_serv_sla
where	nr_seq_ordem		= nr_sequencia_p;

select	coalesce(max(nr_seq_tipo_equip),0)
into STRICT	nr_seq_tipo_equip_w
from	man_equipamento
where	nr_sequencia = nr_seq_equipamento_p;

nr_seq_sla_w			:= 0;
open C01;
loop
fetch C01 into	
	nr_seq_sla_w,
	nr_seq_sla_regra_w;		
EXIT WHEN NOT FOUND; /* apply on C01 */
	nr_seq_sla_w		:= nr_seq_sla_w;
	nr_seq_sla_regra_w	:= nr_seq_sla_regra_w;
End loop;
close C01;
if (coalesce(nr_seq_sla_regra_w,0) > 0) then
	return nr_seq_sla_regra_w;
else
	return 0;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
