CREATE OR REPLACE FUNCTION sla_dashboard_pck.obter_se_dia_util_localizacao (dt_referencia_p timestamp, nr_seq_localizacao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_feriado_w		varchar(01);
ie_dia_util_w		varchar(01)	:= 'N';
dt_referencia_w		timestamp := trunc(dt_referencia_p);


BEGIN

select	coalesce(max('S'), 'N')
into STRICT	ie_feriado_w
from 	man_localizacao_feriado
where 	dt_feriado		= dt_referencia_w
and	nr_seq_localizacao	= nr_seq_localizacao_p;

if (ie_feriado_w = 'N') and (pkg_date_utils.IS_BUSINESS_DAY(dt_referencia_p) = 1) then
	--(to_char(pkg_date_utils.get_WeekDay(dt_referencia_p)) in ('2','3','4','5','6')) then

	ie_dia_util_w	:= 'S';
end if;

return ie_dia_util_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
