CREATE OR REPLACE FUNCTION sla_dashboard_pck.obter_se_estourou_resp ( nr_seq_ordem_serv_p man_sla_monitor.nr_seq_ordem_servico%type, cd_estabelecimento_p bigint ) RETURNS varchar AS $body$
DECLARE


qt_sla_monitor_w		bigint;
ie_estourou_w			varchar(1) := 'N';
dt_inicio_sla_w			man_ordem_serv_sla.dt_inicio_sla%type;
ie_tempo_w			man_ordem_serv_sla.ie_tempo%type;
qt_min_inicio_w			man_ordem_serv_sla.qt_min_inicio%type;
dt_min_resp_w			Man_ordem_serv_estagio.dt_atualizacao%type;
qt_min_total_res_w		bigint;

BEGIN
select	max(dt_inicio_sla),
	max(ie_tempo),
	max(qt_min_inicio)
into STRICT	dt_inicio_sla_w,
	ie_tempo_w,
	qt_min_inicio_w
from	man_ordem_serv_sla
where	nr_seq_ordem = nr_seq_ordem_serv_p;

SELECT coalesce(mose.dt_atualizacao, clock_timestamp())
into STRICT	dt_min_resp_w
FROM man_estagio_processo MEP1,
     Man_ordem_serv_estagio MOSE
WHERE MOSE.nr_seq_ordem      =  nr_seq_ordem_serv_p
AND MEP1.IE_AGUARDA_CLIENTE = 'N'
AND MEP1.nr_sequencia        = MOSE.nr_seq_estagio
AND MOSE.NR_SEQUENCIA = (SELECT Min(MOSE.nr_sequencia)
                        FROM Man_ordem_serv_estagio MOSE
                        WHERE MOSE.nr_seq_ordem      =  nr_seq_ordem_serv_p
                        AND MOSE.NM_USUARIO <> 'WebService');
			
			
if (ie_tempo_w = 'COR') then
	qt_min_total_res_w := dt_min_resp_w - dt_inicio_sla_w;
	
	if (qt_min_total_res_w > qt_min_inicio_w) then
		ie_estourou_w := 'S';
	else
		ie_estourou_w := 'N';
	end if;
elsif (ie_tempo_w = 'COM') then

	qt_min_total_res_w := sla_dashboard_pck.man_obter_min_com(cd_estabelecimento_p, dt_inicio_sla_w, dt_min_resp_w);
	
	if (qt_min_total_res_w > qt_min_inicio_w) then
		ie_estourou_w := 'S';
	else
		ie_estourou_w := 'N';
	end if;
end if;

return ie_estourou_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
