CREATE OR REPLACE FUNCTION sla_dashboard_pck.obter_tempo_comercial_tz (nr_seq_ordem_servico_p bigint, cd_estabelecimento_p bigint, dt_ordem_servico_p timestamp, dt_primeiro_atend_p timestamp) RETURNS bigint AS $body$
DECLARE

		
ie_loc_exterior_w	varchar(1);
nr_seq_localizacao_w	bigint;
qt_min_com_w		bigint;

BEGIN

select 	max(nr_seq_localizacao)
into STRICT	nr_seq_localizacao_w
from	man_ordem_servico
where	nr_sequencia = nr_seq_ordem_servico_p;

ie_loc_exterior_w := sla_dashboard_pck.obter_loc_exterior(nr_seq_ordem_servico_p);

if (ie_loc_exterior_w = 'S') then
	qt_min_com_w := sla_dashboard_pck.obter_tempo_comercial_loc_tz(dt_ordem_servico_p, dt_primeiro_atend_p, 'MC', nr_seq_localizacao_w);
else
	qt_min_com_w := sla_dashboard_pck.man_obter_min_com(cd_estabelecimento_p, dt_ordem_servico_p, dt_primeiro_atend_p);
end if;

return	qt_min_com_w;

END;

