CREATE OR REPLACE FUNCTION sla_dashboard_pck.obter_tipo_tempo_evento ( ie_tipo_sla_param_p text, ie_tipo_sla_p text, ie_tipo_sla_termino_p text, ie_tempo_p text, ie_tempo_termino_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_idioma_w		bigint := 5;
cd_exp_tempo_w		valor_dominio.cd_exp_valor_dominio%type;
ds_tempo_w		valor_dominio.ds_valor_dominio%type;
cd_exp_tempo_termino_w	valor_dominio.cd_exp_valor_dominio%type;
ds_tempo_termino_w	valor_dominio.ds_valor_dominio%type;


BEGIN

nr_seq_idioma_w := coalesce(wheb_usuario_pck.get_nr_seq_idioma,5);

if (ie_tempo_p IS NOT NULL AND ie_tempo_p::text <> '') then

	select	max(vd.cd_exp_valor_dominio),
		max(vd.ds_valor_dominio)
	into STRICT	cd_exp_tempo_w,
		ds_tempo_w
	from	valor_dominio vd
	where	vd.cd_dominio = 1646
	 and	vd.vl_dominio = ie_tempo_p;

end if;

if (ie_tempo_termino_p IS NOT NULL AND ie_tempo_termino_p::text <> '') then

	select	max(vd.cd_exp_valor_dominio),
		max(vd.ds_valor_dominio)
	into STRICT	cd_exp_tempo_termino_w,
		ds_tempo_termino_w
	from	valor_dominio vd
	where	vd.cd_dominio = 1646
	 and	vd.vl_dominio = ie_tempo_termino_p;

end if;

if (cd_exp_tempo_w IS NOT NULL AND cd_exp_tempo_w::text <> '' AND cd_exp_tempo_termino_w IS NOT NULL AND cd_exp_tempo_termino_w::text <> '') then

	if (ie_tipo_sla_p = ie_tipo_sla_param_p and ie_tipo_sla_termino_p = ie_tipo_sla_param_p) then
		ds_retorno_w := substr(obter_desc_expressao_idioma(cd_exp_tempo_w, ds_tempo_w, nr_seq_idioma_w) ||
				' + ' || obter_desc_expressao_idioma(cd_exp_tempo_termino_w, ds_tempo_termino_w, nr_seq_idioma_w),1,255);
	elsif (ie_tipo_sla_p = ie_tipo_sla_param_p and ie_tipo_sla_termino_p <> ie_tipo_sla_param_p) then
		ds_retorno_w := substr(obter_desc_expressao_idioma(cd_exp_tempo_w, ds_tempo_w, nr_seq_idioma_w),1,255);
	elsif (ie_tipo_sla_p <> ie_tipo_sla_param_p and ie_tipo_sla_termino_p = ie_tipo_sla_param_p) then
		ds_retorno_w := substr(obter_desc_expressao_idioma(cd_exp_tempo_termino_w, ds_tempo_termino_w, nr_seq_idioma_w),1,255);
	end if;

elsif ((cd_exp_tempo_w IS NOT NULL AND cd_exp_tempo_w::text <> '') and ie_tipo_sla_p = ie_tipo_sla_param_p) then
	ds_retorno_w := substr(obter_desc_expressao_idioma(cd_exp_tempo_w, ds_tempo_w, nr_seq_idioma_w),1,255);
elsif ((cd_exp_tempo_termino_w IS NOT NULL AND cd_exp_tempo_termino_w::text <> '') and ie_tipo_sla_termino_p = ie_tipo_sla_param_p) then
	ds_retorno_w := substr(obter_desc_expressao_idioma(cd_exp_tempo_termino_w, ds_tempo_termino_w, nr_seq_idioma_w),1,255);

end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
