SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION sla_dashboard_pck.reg_sla_gerencia_all_def (dt_month_p timestamp, ie_type_p text) RETURNS SETOF T_GERENCIA_SLA AS $body$
DECLARE


t_gerencia_sla_row_w	t_gerencia_sla_row;
dt_referencia_w		timestamp;

c01 CURSOR FOR  --Forma do mes Janeiro e Fev
SELECT	nr_seq_gerencia_des,
	nr_seq_gerencia_sup,
	ds_gerencia,
	qt_sla,
	qt_sla_calculo,
	qt_atraso_resposta,
	qt_atraso_solucao,
	pr_atingido,
	qt_target,
	dt_referencia
from	table(sla_dashboard_pck.reg_sla_gerencia_def_ref(dt_month_p, ie_type_p));

c02 CURSOR FOR  --Forma do mes Marco ate Agosto
SELECT	nr_seq_gerencia_des,
	nr_seq_gerencia_sup,
	ds_gerencia,
	qt_sla,
	qt_sla_calculo,
	qt_atraso_resposta,
	qt_atraso_solucao,
	pr_atingido,
	qt_target,
	dt_referencia
from	table(sla_dashboard_pck.registros_sla_gerencia(dt_month_p, ie_type_p));

c01_w	c01%rowtype;
c02_w	c02%rowtype;


BEGIN

dt_referencia_w	:= trunc(dt_month_p,'month');

if (trunc(dt_referencia_w, 'mm') > trunc(to_date('01/08/2020','dd/mm/yyyy'),'MM')) then
	
	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

		t_gerencia_sla_row_w.ds_gerencia		:= obter_nome_gerencia( coalesce( c01_w.nr_seq_gerencia_des, c01_w.nr_seq_gerencia_sup ));
		t_gerencia_sla_row_w.nr_seq_gerencia_des	:= c01_w.nr_seq_gerencia_des;
		t_gerencia_sla_row_w.nr_seq_gerencia_sup	:= c01_w.nr_seq_gerencia_sup;
		t_gerencia_sla_row_w.dt_referencia		:= dt_referencia_w;
		t_gerencia_sla_row_w.qt_atraso_resposta		:= c01_w.qt_atraso_resposta;
		t_gerencia_sla_row_w.qt_atraso_solucao		:= c01_w.qt_atraso_solucao;
		t_gerencia_sla_row_w.qt_sla			:= c01_w.qt_sla;
		t_gerencia_sla_row_w.qt_sla_calculo		:= c01_w.qt_sla_calculo;
		t_gerencia_sla_row_w.pr_atingido		:= c01_w.pr_atingido;
		t_gerencia_sla_row_w.qt_target			:= sla_dashboard_pck.obter_meta_sla(dt_referencia_w, ie_type_p);

		RETURN NEXT t_gerencia_sla_row_w;
	end loop;
	close C01;
	
else

	open C02;
	loop
	fetch C02 into
		c02_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */

		t_gerencia_sla_row_w.ds_gerencia		:= obter_nome_gerencia( coalesce( c02_w.nr_seq_gerencia_des, c02_w.nr_seq_gerencia_sup ));
		t_gerencia_sla_row_w.nr_seq_gerencia_des	:= c02_w.nr_seq_gerencia_des;
		t_gerencia_sla_row_w.nr_seq_gerencia_sup	:= c02_w.nr_seq_gerencia_sup;
		t_gerencia_sla_row_w.dt_referencia		:= dt_referencia_w;
		t_gerencia_sla_row_w.qt_atraso_resposta		:= c02_w.qt_atraso_resposta;
		t_gerencia_sla_row_w.qt_atraso_solucao		:= c02_w.qt_atraso_solucao;
		t_gerencia_sla_row_w.qt_sla			:= c02_w.qt_sla;
		t_gerencia_sla_row_w.qt_sla_calculo		:= c02_w.qt_sla_calculo;
		t_gerencia_sla_row_w.pr_atingido		:= c02_w.pr_atingido;
		t_gerencia_sla_row_w.qt_target			:= sla_dashboard_pck.obter_meta_sla(dt_referencia_w, ie_type_p);

		RETURN NEXT t_gerencia_sla_row_w;
	end loop;
	close C02;

end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
