CREATE OR REPLACE FUNCTION sla_dashboard_pck.reg_sla_prioridade_alldef (dt_month_p timestamp, ie_type_p text) RETURNS SETOF T_PRIORIDADE_SLA_REF AS $body$
DECLARE


t_prioridade_sla_ref_row_w	t_prioridade_sla_ref_row;
dt_referencia_w		timestamp;

c01 CURSOR FOR  --Forma do mes Janeiro e Fev
SELECT  a.ds_prioridade,
	a.ie_prioridade,
	a.qt_sla,
	a.qt_atraso_resposta,
	a.qt_atraso_solucao,
	a.qt_sla_calculo
from  table(sla_dashboard_pck.reg_sla_prior_def_ref(dt_month_p, ie_type_p)) a;

c02 CURSOR FOR  --Forma do mes Marco ate Agosto
SELECT	a.ds_prioridade,
	a.ie_prioridade,
	a.qt_sla,
	a.qt_atraso_resposta,
	a.qt_atraso_solucao,
	a.qt_sla_calculo
from	table(sla_dashboard_pck.registros_sla_prioridade(dt_month_p, ie_type_p)) a;

c01_w	c01%rowtype;
c02_w	c02%rowtype;


BEGIN

dt_referencia_w	:= trunc(dt_month_p,'month');

if (trunc(dt_referencia_w, 'mm') > trunc(to_date('01/08/2020','dd/mm/yyyy'),'MM')) then
	
	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

		t_prioridade_sla_ref_row_w.ds_prioridade		:= c01_w.ds_prioridade;
		t_prioridade_sla_ref_row_w.ie_prioridade		:= c01_w.ie_prioridade;
		t_prioridade_sla_ref_row_w.dt_referencia		:= dt_referencia_w;
		t_prioridade_sla_ref_row_w.qt_atraso_resposta		:= c01_w.qt_atraso_resposta;
		t_prioridade_sla_ref_row_w.qt_atraso_solucao		:= c01_w.qt_atraso_solucao;
		t_prioridade_sla_ref_row_w.qt_sla			:= c01_w.qt_sla;
		t_prioridade_sla_ref_row_w.qt_sla_calculo		:= c01_w.qt_sla_calculo;
	
		RETURN NEXT t_prioridade_sla_ref_row_w;
	end loop;
	close C01;
	
else

	open C02;
	loop
	fetch C02 into
		c02_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */

		t_prioridade_sla_ref_row_w.ds_prioridade		:= c02_w.ds_prioridade;
		t_prioridade_sla_ref_row_w.ie_prioridade		:= c02_w.ie_prioridade;
		t_prioridade_sla_ref_row_w.dt_referencia		:= dt_referencia_w;
		t_prioridade_sla_ref_row_w.qt_atraso_resposta		:= c02_w.qt_atraso_resposta;
		t_prioridade_sla_ref_row_w.qt_atraso_solucao		:= c02_w.qt_atraso_solucao;
		t_prioridade_sla_ref_row_w.qt_sla			:= c02_w.qt_sla;
		t_prioridade_sla_ref_row_w.qt_sla_calculo		:= c02_w.qt_sla_calculo;
	
		RETURN NEXT t_prioridade_sla_ref_row_w;
	end loop;
	close C02;

end if;

END;

