CREATE OR REPLACE FUNCTION sla_dashboard_pck.registros_sla_geral_referencia (dt_referencia_p timestamp) RETURNS SETOF T_CHART_SLA_GERAL_GLOBAL AS $body$
DECLARE


t_chart_sla_geral_global_row_w	t_chart_sla_geral_global_row;
dt_referencia_w		timestamp;

c01 CURSOR FOR  --Forma do mes Janeiro e Fev
SELECT  ds_tipo_sla,
	ie_tipo_sla,
	qt_sla,
	qt_sla_calculo,
	qt_atraso_resposta,
	qt_atraso_solucao,
	pr_atingido sla_achievement,
	qt_target
from    table(sla_dashboard_pck.registros_sla_dashboard(dt_referencia_w));

c02 CURSOR FOR  --Forma do mes Marco ate Agosto
SELECT   ds_tipo_sla,
	 ie_tipo_sla,
	 null qt_sla,
	 null qt_sla_calculo,
	 null qt_atraso_resposta,
	 null qt_atraso_solucao,
	 percent_atingido sla_achievement,
	 percent_meta qt_target
from    table(sla_dashboard_pck.registros_sla_geral(dt_referencia_w));

c03 CURSOR FOR  --Forma do mes de Setembro 2020 adiante
SELECT  ds_tipo_sla,
	ie_tipo_sla,
	qt_sla,
	qt_sla_calculo,
	qt_atraso_resposta,
	qt_atraso_solucao,
	sla_achievement,
	qt_target
from  	table(sla_dashboard_pck.registros_sla_geral_global(dt_referencia_w));

c01_w	c01%rowtype;
c02_w	c02%rowtype;
c03_w	c03%rowtype;


BEGIN

dt_referencia_w	:= trunc(dt_referencia_p,'month');

if (trunc(dt_referencia_w, 'mm') <= trunc(to_date('01/01/2020','dd/mm/yyyy'),'MM'))
	or (trunc(dt_referencia_w, 'mm') = trunc(to_date('01/02/2020','dd/mm/yyyy'),'MM')) then
	
	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		
		t_chart_sla_geral_global_row_w.ds_tipo_sla			:= c01_w.ds_tipo_sla;
		t_chart_sla_geral_global_row_w.dt_referencia			:= dt_referencia_w;
		t_chart_sla_geral_global_row_w.sla_achievement			:= c01_w.sla_achievement;
		t_chart_sla_geral_global_row_w.qt_target			:= c01_w.qt_target;
		t_chart_sla_geral_global_row_w.ie_tipo_sla			:= c01_w.ie_tipo_sla;
		t_chart_sla_geral_global_row_w.qt_sla				:= c01_w.qt_sla;
		t_chart_sla_geral_global_row_w.qt_sla_calculo			:= c01_w.qt_sla_calculo;
		t_chart_sla_geral_global_row_w.qt_atraso_resposta		:= c01_w.qt_atraso_resposta;
		t_chart_sla_geral_global_row_w.qt_atraso_solucao		:= c01_w.qt_atraso_solucao;
	
		RETURN NEXT t_chart_sla_geral_global_row_w;
	
	end loop;
	close C01;
	
elsif (trunc(dt_referencia_w, 'mm') >= trunc(to_date('01/03/2020','dd/mm/yyyy'),'MM')) and (trunc(dt_referencia_w, 'mm') <= trunc(to_date('01/08/2020','dd/mm/yyyy'),'MM')) then
	
	open C02;
	loop
	fetch C02 into
		c02_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	
		t_chart_sla_geral_global_row_w.ds_tipo_sla			:= c02_w.ds_tipo_sla;
		t_chart_sla_geral_global_row_w.dt_referencia			:= dt_referencia_w;
		t_chart_sla_geral_global_row_w.sla_achievement			:= c02_w.sla_achievement;
		t_chart_sla_geral_global_row_w.qt_target			:= c02_w.qt_target;
		t_chart_sla_geral_global_row_w.ie_tipo_sla			:= c02_w.ie_tipo_sla;
		t_chart_sla_geral_global_row_w.qt_sla				:= c02_w.qt_sla;
		t_chart_sla_geral_global_row_w.qt_sla_calculo			:= c02_w.qt_sla_calculo;
		t_chart_sla_geral_global_row_w.qt_atraso_resposta		:= c02_w.qt_atraso_resposta;
		t_chart_sla_geral_global_row_w.qt_atraso_solucao		:= c02_w.qt_atraso_solucao;
	
		RETURN NEXT t_chart_sla_geral_global_row_w;
	
	end loop;
	close C02;
	
else

	open C03;
	loop
	fetch C03 into
		c03_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		
		t_chart_sla_geral_global_row_w.ds_tipo_sla			:= c03_w.ds_tipo_sla;
		t_chart_sla_geral_global_row_w.dt_referencia			:= dt_referencia_w;
		t_chart_sla_geral_global_row_w.sla_achievement			:= c03_w.sla_achievement;
		t_chart_sla_geral_global_row_w.qt_target			:= c03_w.qt_target;
		t_chart_sla_geral_global_row_w.ie_tipo_sla			:= c03_w.ie_tipo_sla;
		t_chart_sla_geral_global_row_w.qt_sla				:= c03_w.qt_sla;
		t_chart_sla_geral_global_row_w.qt_sla_calculo			:= c03_w.qt_sla_calculo;
		t_chart_sla_geral_global_row_w.qt_atraso_resposta		:= c03_w.qt_atraso_resposta;
		t_chart_sla_geral_global_row_w.qt_atraso_solucao		:= c03_w.qt_atraso_solucao;
	
		RETURN NEXT t_chart_sla_geral_global_row_w;
	
	end loop;
	close C03;

end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
