DECLARE


t_localizacao_sla_ref_row_w	t_localizacao_sla_ref_row;
dt_referencia_w		timestamp;

c01 CURSOR FOR  --Forma do mes Janeiro e Fev
SELECT	nr_seq_localizacao,
