from	table(sla_dashboard_pck.registros_sla_localizacao_ref(dt_month_p, ie_type_p));

c02 CURSOR FOR  --Forma do mes Marco ate Agosto
SELECT	nr_seq_localizacao,
	ds_localizacao,
	qt_sla,
	qt_sla_calculo,
	qt_atraso_resposta,
