		t_localizacao_sla_ref_row_w.ds_sla_detail		:= c01_w.ds_sla_detail;
		t_localizacao_sla_ref_row_w.qt_target			:= c01_w.qt_target;
		t_localizacao_sla_ref_row_w.cd_cnpj			:= c01_w.cd_cnpj;
	
		RETURN NEXT t_localizacao_sla_ref_row_w;
	end loop;
	close C01;
	
else

	open C02;
	loop
	fetch C02 into
		c02_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */

		if (c02_w.nr_seq_localizacao IS NOT NULL AND c02_w.nr_seq_localizacao::text <> '') then
			t_localizacao_sla_ref_row_w.ds_localizacao		:= substr(obter_desc_man_localizacao(c02_w.nr_seq_localizacao),1,255);
		else
			t_localizacao_sla_ref_row_w.ds_localizacao		:= c02_w.ds_localizacao;
		end if;
		t_localizacao_sla_ref_row_w.nr_seq_localizacao		:= c02_w.nr_seq_localizacao;
		t_localizacao_sla_ref_row_w.dt_referencia		:= dt_referencia_w;
		t_localizacao_sla_ref_row_w.qt_atraso_resposta		:= c02_w.qt_atraso_resposta;
		t_localizacao_sla_ref_row_w.qt_atraso_solucao		:= c02_w.qt_atraso_solucao;
		t_localizacao_sla_ref_row_w.qt_sla			:= c02_w.qt_sla;
		t_localizacao_sla_ref_row_w.qt_sla_calculo		:= c02_w.qt_sla_calculo;
		t_localizacao_sla_ref_row_w.pr_atingido			:= c02_w.pr_atingido;
		t_localizacao_sla_ref_row_w.ds_sla_detail		:= c02_w.ds_sla_detail;
		t_localizacao_sla_ref_row_w.qt_target			:= c02_w.qt_target;
