SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE sncm_controle_mat_pck.enviar_arquivo (nr_seq_lote_p controle_mat_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

	
	ds_retorno_w		text;
				
	c01 CURSOR(	nr_seq_lote_pc	 controle_mat_lote.nr_sequencia%type ) FOR
		SELECT	nr_sequencia nr_seq_arquivo
		from	controle_mat_arquivo
		where	nr_seq_lote = nr_seq_lote_pc
		and	ie_status in ('1','2'); -- Aguardando envio / Possui inconsistencias
	
	
BEGIN
	
	update	controle_mat_lote
	set	dt_ini_envio	= clock_timestamp(),
		ie_status	= '5', -- Enviando arquivos
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_lote_p;
	commit;
	
	-- Fazer o envio
	for c01_w in c01( nr_seq_lote_p ) loop
		-- Excluir o retorno
		delete	FROM controle_mat_arq_xml
		where	nr_seq_arquivo = c01_w.nr_seq_arquivo;	
	
		-- Excluir o retorno
		delete	FROM controle_mat_arquivo_ret
		where	nr_seq_arquivo = c01_w.nr_seq_arquivo;
		
		ds_retorno_w := bifrost.send_integration(nm_event => 'sncm.file.send',
							nm_javaclass => 'com.philips.tasy.integration.sncm.sncmfile.outbound.SncmFileCallback',
							ds_arguments => to_char(c01_w.nr_seq_arquivo),
							nm_user => nm_usuario_p);
		
		update	controle_mat_arquivo
		set	dt_envio	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= c01_w.nr_seq_arquivo;
	end loop;

	-- Pegar os retornos - Tem que ser via job porque o retorno nao e sincrono
	CALL sncm_controle_mat_pck.obter_retorno_job(nr_seq_lote_p => nr_seq_lote_p, nm_usuario_p => nm_usuario_p, minutes_p => 0.25);
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE sncm_controle_mat_pck.enviar_arquivo (nr_seq_lote_p controle_mat_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
