CREATE OR REPLACE PROCEDURE sncm_controle_mat_pck.marcar_envio_arquivo ( nr_seq_arquivo_p controle_mat_arquivo.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN
	
	-- Excluir o retorno
