CREATE OR REPLACE PROCEDURE sncm_controle_mat_pck.valida_se_existe_arquivo ( nr_seq_lote_p controle_mat_lote.nr_sequencia%type) AS $body$
DECLARE

	
	nr_seq_arquivo_w	controle_mat_arquivo.nr_sequencia%type;
	
	
BEGIN
	
	select	max(nr_sequencia)
	into STRICT	nr_seq_arquivo_w
	from	controle_mat_arquivo
	where	nr_seq_lote = nr_seq_lote_p;

	if (coalesce(nr_seq_arquivo_w::text, '') = '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(nr_seq_mensagem_p => 1206608);
	end if;
	
	END;

$body$
