SET client_encoding TO 'UTF8';


	--##### RETRY_MESSAGES
CREATE OR REPLACE PROCEDURE spinal_pck.retry_messages (qt_attempt_p bigint) AS $body$
DECLARE

	
  messages_w RECORD;

BEGIN

	for messages_w in (SELECT message_id,
			cd_establishment
		from 	spinal_messages a
		where 	a.ds_status		= 'ERROR'
		and	a.qt_attempt 	< qt_attempt_p
		order by dt_message desc
		for update) loop
		begin
		PUBLISH_SPINAL_MESSAGE(messages_w.message_id, messages_w.cd_establishment);
		end;
	end loop;

	commit;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE spinal_pck.retry_messages (qt_attempt_p bigint) FROM PUBLIC;
