CREATE OR REPLACE FUNCTION suep_md_pck.get_color_hum_color_icon_md ( fld_nm_sub_content text, last_return text ) RETURNS varchar AS $body$
DECLARE

        ds_ext_red_w    CONSTANT varchar(17) := upper('thirdCell:#e0362f');
		ds_ext_yellow_w CONSTANT varchar(17) := upper('thirdCell:#ffcd06');
   		ds_gray_w       CONSTANT varchar(4) := 'gray';
   		ds_yellow_w     CONSTANT varchar(6) := 'yellow';
   		ds_red_w        CONSTANT varchar(3) := 'red';
	
BEGIN
		if (last_return <> ds_ext_red_w and last_return <> ds_red_w) then
			if (upper(fld_nm_sub_content) = ds_ext_red_w) then
				return 'red';
			elsif (upper(fld_nm_sub_content) = ds_ext_yellow_w) then
                return 'yellow';
			elsif (last_return = ds_gray_w) then
                return 'gray';
            end if;
        else
            return 'red';
        end if;
	end;


$body$
