CREATE OR REPLACE FUNCTION suep_md_pck.get_color_point_graf_md ( nr_pontuacao_p bigint, nr_baixa_severa_suep bigint, nr_alta_severa_suep bigint, nr_baixa_moderada_suep bigint, nr_alta_moderada_suep bigint ) RETURNS varchar AS $body$
BEGIN
		if ((nr_pontuacao_p < nr_baixa_severa_suep) or (nr_pontuacao_p > nr_alta_severa_suep)) then
            return 'red';
         elsif ((nr_pontuacao_p >= nr_baixa_severa_suep AND nr_pontuacao_p <= nr_baixa_moderada_suep) or
               (nr_pontuacao_p <= nr_alta_severa_suep AND nr_pontuacao_p >= nr_alta_moderada_suep)) then
            return 'yellow';
         end if;
		return 'gray';
	end;


$body$
