CREATE OR REPLACE FUNCTION suep_pck.getstatusdia ( nr_atendimento_p bigint,nr_seq_meta_p bigint, nr_dia_p bigint) RETURNS varchar AS $body$
DECLARE

					 
ie_status_w	varchar(10);

C001 CURSOR FOR 
	SELECT	coalesce(b.ie_status,'I') ie_status 
	from	tof_meta_atend a, 
		tof_meta_atend_hor b, 
		tof_meta c 
	where	a.nr_sequencia = b.nr_seq_meta_atend 
	and 	a.nr_seq_meta = c.nr_sequencia 
	and	c.nr_sequencia	= nr_seq_meta_p 
	and	trunc(b.dt_geracao) = trunc(clock_timestamp() - nr_dia_p) 
	and	c.ie_tipo_meta = 'D' 
	and	Obter_se_meta_liberada(a.nr_atendimento,a.nr_seq_meta) = 'S' 
	and	a.nr_atendimento = nr_atendimento_p 
	order by dt_geracao;


BEGIN 
 
open C001;
loop 
fetch C001 into	 
	ie_status_w;
EXIT WHEN NOT FOUND; /* apply on C001 */
end loop;
close C001;
 
return	coalesce(ie_status_w,'I');
 
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
