SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION suep_pck.obter_controle_imunizacao ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, nr_seq_suep_p bigint, nm_usuario_p text ) RETURNS SETOF T_IMMUNIZATION_ROW_DATA AS $body$
DECLARE

			 
t_immunization_row_w	t_immunization_row;

C01 CURSOR FOR 
	SELECT	substr(obter_descricao_padrao('VACINA','DS_VACINA',b.nr_sequencia),1,500) ds_vacina, 
		substr(obter_valor_dominio(1018,ie_dose),1,200) ds_dose 
	from 	vacina b, 
		vacina_calendario c 
	where	b.nr_sequencia	= c.nr_seq_vacina 
	and	((clock_timestamp()-coalesce(to_Date(obter_dados_pf(cd_pessoa_fisica_p,'DN'),'dd/mm/yyyy'),clock_timestamp())) between Obter_idade_min_max_vacina(c.nr_sequencia,'MIN') and Obter_idade_min_max_vacina(c.nr_sequencia,'MAX')) 
	and (Obter_idade_min_max_vacina(c.nr_sequencia,'MIN') IS NOT NULL AND (Obter_idade_min_max_vacina(c.nr_sequencia,'MIN'))::text <> '') 
	and (Obter_idade_min_max_vacina(c.nr_sequencia,'MAX') IS NOT NULL AND (Obter_idade_min_max_vacina(c.nr_sequencia,'MAX'))::text <> '') 
	and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') 
	and  not exists (SELECT 1 from paciente_vacina e where e.nr_seq_vacina = b.nr_sequencia and e.cd_pessoa_fisica = cd_pessoa_fisica_p and c.ie_dose = e.ie_dose) 
	order by 1,2;
BEGIN
 
for r_c01 in c01 loop 
	begin 
	t_immunization_row_w.ds_vacina		:= r_c01.ds_vacina;
	t_immunization_row_w.ds_dose		:= r_c01.ds_dose;
	RETURN NEXT t_immunization_row_w;
	end;
end loop;
 
return;
 
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
