SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION suep_pck.obter_dialise ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, nr_seq_suep_p bigint, nm_usuario_p text ) RETURNS SETOF T_CPOE_ROW_DATA AS $body$
DECLARE

			 
t_dialise_row_w		t_cpoe_row;

 
C01 CURSOR FOR 
	SELECT	substr(obter_valor_dominio(1934,b.ie_tipo_hemodialise),1,255) ds_tipo_hemodialise, 
		substr(obter_valor_dominio(1936,b.ie_ultrafiltracao),1,255) ds_ultrafiltracao 
	FROM	prescr_medica a, 
		hd_prescricao b 
	WHERE	a.nr_prescricao = b.nr_prescricao  
	and 	a.nr_atendimento = nr_atendimento_p 
	and	coalesce(a.dt_suspensao::text, '') = '' 
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
	and 	clock_timestamp() between a.dt_inicio_prescr AND	a.dt_validade_prescr 
	ORDER BY a.dt_prescricao desc;

 
BEGIN 
 
 
for r_c01 in c01 loop 
	begin 
	t_dialise_row_w.ds_item 		:= r_c01.ds_tipo_hemodialise;
	t_dialise_row_w.ds_ultrafiltracao	:= r_c01.ds_ultrafiltracao;
	t_dialise_row_w.ie_tipo			:= 'DIAL';
	 
	RETURN NEXT t_dialise_row_w;
	end;
end loop;
 
 
return;
 
 
END;		

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
