CREATE OR REPLACE FUNCTION suep_pck.obter_medicamento ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, nr_seq_suep_p bigint, nm_usuario_p text ) RETURNS SETOF T_CPOE_ROW_DATA AS $body$
DECLARE

			 
t_medicamento_row_w		t_cpoe_row;

 
C01 CURSOR FOR 
	SELECT	distinct x.ds_intervalo, 
		a.cd_unidade_medida_dose cd_unidade_medida_dose, 
		Substr(Obter_Desc_Material(A.Cd_Material),1,80) Ds_Material, 
		a.ie_via_aplicacao, 
		a.qt_dose 
	FROM prescr_material a
LEFT OUTER JOIN prescr_medica f ON (a.nr_prescricao = f.nr_prescricao)
LEFT OUTER JOIN intervalo_prescricao x ON (a.cd_intervalo = x.cd_intervalo)
WHERE f.nr_atendimento = nr_atendimento_p and clock_timestamp() Between F.Dt_Inicio_Prescr AND F.Dt_Validade_Prescr and coalesce(a.nr_sequencia_solucao::text, '') = '' and coalesce(a.nr_sequencia_dieta::text, '') = '' and coalesce(a.nr_sequencia_diluicao::text, '') = '' and coalesce(a.cd_kit_material::text, '') = '' and a.ie_agrupador = 1 and (f.dt_liberacao IS NOT NULL AND f.dt_liberacao::text <> '') and coalesce(f.dt_suspensao::text, '') = '' and coalesce(a.dt_suspensao::text, '') = '' ORDER BY ds_material;

 
BEGIN 
 
 
for r_c01 in c01 loop 
	begin 
	t_medicamento_row_w.ds_item 		:= r_c01.Ds_Material;
	t_medicamento_row_w.ds_intervalo	:= r_c01.ds_intervalo;
	t_medicamento_row_w.ie_via_aplicacao	:= r_c01.ie_via_aplicacao;
	t_medicamento_row_w.cd_unidade_medida	:= r_c01.cd_unidade_medida_dose;
	t_medicamento_row_w.qt_dose		:= r_c01.qt_dose;
	t_medicamento_row_w.ie_tipo		:= 'MED';
	 
	RETURN NEXT t_medicamento_row_w;
	end;
end loop;
 
 
return;
 
 
END;		

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
