CREATE OR REPLACE FUNCTION suep_pck.obter_prenatal ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, nr_seq_suep_p bigint, nm_usuario_p text ) RETURNS SETOF T_PRENATAL_ROW_DATA AS $body$
DECLARE

			 
t_prenatal_row_w	t_prenatal_row;

dt_evolucao_w		timestamp;
qt_ig_sem_w		med_pac_pre_natal_evol.qt_ig_sem%type;
qt_ig_dia_w		med_pac_pre_natal_evol.qt_ig_dia%type;
qt_ig_sem_us_w		med_pac_pre_natal_evol.qt_ig_sem_us%type;
qt_ig_dia_us_w		med_pac_pre_natal_evol.qt_ig_dia_us%type;
nr_seq_evolucao_w	med_pac_pre_natal_evol.nr_seq_evolucao%type;
ds_dinamica_uterina_w  varchar(255);
qt_contracoes_w		enf_tri_obstetrica.qt_contracoes%type;
ds_evolucao_w		text;

C01 CURSOR FOR 
	SELECT	a.nr_atendimento nr_atendimento, 
		a.nr_sequencia nr_sequencia, 
		a.dt_ultima_menstruacao dum, 
		a.qt_ig_semana qt_ig_semana, 
		a.qt_ig_dia qt_ig_dia, 
		a.dt_prov_parto dt_prov_parto, 
		a.dt_us dt_us, 
		a.qt_ig_sem_us qt_ig_sem_us, 
		a.qt_ig_dia_us qt_ig_dia_us, 
		a.dt_prov_parto_us dt_prov_parto_us, 
		a.qt_gestacoes qt_gestacoes, 
		a.qt_natimortos qt_natimortos, 
		a.qt_parto_normal qt_parto_normal, 
		a.qt_filhos_vivos qt_filhos_vivos, 
		a.qt_filhos_mortos qt_filhos_mortos, 
		a.qt_abortos qt_abortos 
	from	med_pac_pre_natal a 
	where 	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
	and	a.nr_sequencia	  = (SELECT 	max(b.nr_sequencia) 
				   from	med_pac_pre_natal b 
				   where	b.cd_pessoa_fisica = a.cd_pessoa_fisica 
				   and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> ''));

 
BEGIN 
 
 
for r_c01 in c01 loop 
	begin 
	 
	 
	 
	select max(dt_evolucao), 
	   max(qt_ig_sem), 
	   max(qt_ig_dia), 
	   max(qt_ig_sem_us), 
	   max(qt_ig_dia_us), 
	   max(nr_seq_evolucao) 
	into STRICT	dt_evolucao_w, 
	   qt_ig_sem_w, 
	   qt_ig_dia_w, 
	   qt_ig_sem_us_w, 
	   qt_ig_dia_us_w, 
	   nr_seq_evolucao_w 
	from 	med_pac_pre_natal_evol 
	where	nr_seq_pre_natal = r_c01.nr_sequencia;
 
	 
	select max(qt_contracoes) qt_contracoes, 
	   substr(obter_valor_dominio(3895,max(ie_dinamica_uterina)),1,255) ds_dinamica 
	into STRICT qt_contracoes_w, 
	  ds_dinamica_uterina_w 
	from enf_tri_obstetrica 
	where  cd_pessoa_fisica = cd_pessoa_fisica_p;
		 
	/*select max(ds_evolucao)	 
	into  ds_evolucao_w 
	from  med_evolucao 
	where nr_sequencia = nr_seq_evolucao_w;*/
 
	 
	 
	t_prenatal_row_w.dt_evolucao		:= dt_evolucao_w;
	t_prenatal_row_w.dt_ultima_menstruacao	:= r_c01.dum;
	t_prenatal_row_w.qt_ig_semana		:= r_c01.qt_ig_semana;
	t_prenatal_row_w.qt_ig_dia		:= r_c01.qt_ig_dia;
	t_prenatal_row_w.qt_ig_sem		:= qt_ig_sem_w;
	t_prenatal_row_w.qt_ig_dia_ev		:= qt_ig_dia_w;
	t_prenatal_row_w.dt_prov_parto		:= r_c01.dt_prov_parto;
	t_prenatal_row_w.dt_us			:= r_c01.dt_us;
	t_prenatal_row_w.qt_ig_sem_us		:= r_c01.qt_ig_sem_us;
	t_prenatal_row_w.qt_ig_dia_us		:= r_c01.qt_ig_dia_us;
	t_prenatal_row_w.qt_ig_sem_us_ev	:= qt_ig_sem_us_w;
	t_prenatal_row_w.qt_ig_dia_us_ev	:= qt_ig_dia_us_w;
	t_prenatal_row_w.dt_prov_parto_us	:= r_c01.dt_prov_parto_us;
	t_prenatal_row_w.qt_gestacoes		:= r_c01.qt_gestacoes;
	t_prenatal_row_w.qt_natimortos		:= r_c01.qt_natimortos;
	t_prenatal_row_w.qt_parto_normal	:= r_c01.qt_parto_normal;
	t_prenatal_row_w.qt_filhos_vivos	:= r_c01.qt_filhos_vivos;
	t_prenatal_row_w.qt_filhos_mortos	:= r_c01.qt_filhos_mortos;
	t_prenatal_row_w.qt_abortos		:= r_c01.qt_abortos;
	t_prenatal_row_w.ie_dinamica_uterina  := ds_dinamica_uterina_w;
	t_prenatal_row_w.qt_contracoes		:= qt_contracoes_w;
	--t_prenatal_row_w.ds_evolucao			:= to_clob(ds_evolucao_w); 
	 
	RETURN NEXT t_prenatal_row_w;
	 
	end;
end loop;
 
 
 
return;
 
END;

