CREATE OR REPLACE FUNCTION suep_pck.obter_procedimentos ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, nr_seq_suep_p bigint, nm_usuario_p text ) RETURNS SETOF T_CPOE_ROW_DATA AS $body$
DECLARE

			 
t_procedimento_row_w			t_cpoe_row;

 
C01 CURSOR FOR 
	SELECT distinct substr(obter_desc_prescr_proc(d.cd_procedimento,d.ie_origem_proced, d.nr_seq_proc_interno),1,240) ds_item, 
		PKG_DATE_FORMATERS.to_varchar(d.dt_prev_execucao ,'timestamp',obter_estabelecimento_ativo, obter_usuario_ativo) ds_data_prevista, 
		d.dt_prev_execucao 
	from	prescr_medica a, 
		prescr_procedimento d 
	where 	a.nr_prescricao = d.nr_prescricao 
	and 	nr_atendimento = nr_atendimento_p 
	and 	clock_timestamp() between a.dt_inicio_prescr and	a.dt_validade_prescr 
	and 	(d.cd_procedimento IS NOT NULL AND d.cd_procedimento::text <> '') 
	and	coalesce(a.dt_suspensao::text, '') = '' 
	and	coalesce(d.dt_suspensao::text, '') = '' 
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
	order by d.dt_prev_execucao desc;

			 
BEGIN 
 
 
for r_c01 in c01 loop 
	begin 
	t_procedimento_row_w.ds_item 		:= r_c01.ds_item;
	t_procedimento_row_w.dt_prev_execucao	:= r_c01.dt_prev_execucao;
	t_procedimento_row_w.ie_tipo		:= 'PROC';
	RETURN NEXT t_procedimento_row_w;
	end;
end loop;
 
 
return;
 
END;

$body$
