CREATE OR REPLACE FUNCTION suep_pck.obter_vacinas ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, nr_seq_suep_p bigint, nm_usuario_p text ) RETURNS SETOF T_VACCINES_ROW_DATA AS $body$
DECLARE

			 
t_vaccines_row_w	t_vaccines_row;

C01 CURSOR FOR 
	SELECT 	a.nr_seq_vacina, 
		a.dt_registro , 
		substr(obter_descricao_padrao('VACINA','DS_VACINA',a.nr_seq_vacina),1,255) ds_vacina, 
		substr(obter_valor_dominio(1018, a.ie_dose), 1, 100) ds_dose 
	from	paciente_vacina a 
	where	1 = 1 
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
	order by a.dt_registro desc;
BEGIN
 
for r_c01 in c01 loop 
	begin 
	t_vaccines_row_w.nr_seq_vacina		:= r_c01.nr_seq_vacina;
	t_vaccines_row_w.dt_registro		:= r_c01.dt_registro;
	t_vaccines_row_w.ds_vacina		:= r_c01.ds_vacina;
	t_vaccines_row_w.ds_dose		:= r_c01.ds_dose;
	RETURN NEXT t_vaccines_row_w;
	end;
end loop;
 
return;
 
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
