CREATE OR REPLACE PROCEDURE surgery_case_review_pck.surgery_case_review_call ( nr_cirurgia_p bigint default null, nr_sequencia_p bigint default null, ie_type_p text DEFAULT NULL, nm_usuario_p text DEFAULT NULL, ie_all_records_p text default null, ds_comment_p revisao_cirurgia_obs.ds_observao%type default null) AS $body$
BEGIN

	--G - generate_case_review
	if (ie_type_p = 'G') then
		CALL surgery_case_review_pck.external_disable(nr_cirurgia_p, nm_usuario_p);
