	elsif (ie_type_p = 'D') then
		CALL surgery_case_review_pck.disable_record(nr_sequencia_p, nm_usuario_p, ie_all_records_p );
	--R - create remark record
	elsif (ie_type_p in ('RI','RF')) then
		CALL surgery_case_review_pck.create_chart_remark(nr_cirurgia_p, nr_sequencia_p, nm_usuario_p,ie_type_p, ds_comment_p);
	end if;

END;			

