SET client_encoding TO 'UTF8';


--Counts the amount of words in a string.
CREATE OR REPLACE FUNCTION tasyautocomplete.countwords ( pText text ) RETURNS integer AS $body$
DECLARE

lText varchar(2000);

BEGIN
--The line below works for Oracle 11g and above only
--RETURN regexp_count(pText, '[^[:space:]]+');
--First I remove all short words that I wont consider for counting
lText := regexp_replace(pText, ' +[^[:space:]]{0,2}( +|$)', ' ');

--The line below works for all Oracle versions
RETURN LENGTH(regexp_replace(lText, ' [^[:space:]]+ ', 'w'));
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
