SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE tax_dmed_ir_pck.generate_data (batch_monthly_sequence_p tax_dmed_lote_mensal.nr_sequencia%type) AS $body$
DECLARE

      qt_arquivo_gerado_w   integer;
      qt_ano_distinto_w     integer;
      dt_referencia_w       timestamp;
      nr_seq_lote_w         tax_dmed_lote.nr_sequencia%type;

BEGIN
      select  count(1)
      into STRICT    qt_arquivo_gerado_w
      from    tax_dmed_lote a,
              tax_dmed_lote_mensal b,
	      tax_dmed_controle c
      where   (c.dt_geracao IS NOT NULL AND c.dt_geracao::text <> '')
      and     a.nr_sequencia = c.nr_seq_lote
      and     a.nr_sequencia = b.nr_seq_lote
      and     b.nr_sequencia = batch_monthly_sequence_p;

      if (qt_arquivo_gerado_w > 0) then
             CALL wheb_mensagem_pck.exibir_mensagem_abort(1131591);
      end if;

      select  max(dt_referencia),
              max(nr_seq_lote)
      into STRICT    dt_referencia_w,
              nr_seq_lote_w
      from    tax_dmed_lote_mensal
      where   nr_sequencia = batch_monthly_sequence_p;

      select  count(1)
      into STRICT    qt_ano_distinto_w
      from    tax_dmed_lote_mensal 
      where   trunc(dt_referencia,'yyyy') <> trunc(dt_referencia_w,'yyyy')
      and     nr_seq_lote = nr_seq_lote_w;

      if (qt_ano_distinto_w > 0) then
              CALL wheb_mensagem_pck.exibir_mensagem_abort(1131590);
      end if;
      
      PERFORM set_config('tax_dmed_ir_pck.batch_monthly_sequence', batch_monthly_sequence_p, false);
      CALL tax_dmed_ir_pck.load_general_rule();

      delete from tax_dmed_log_comando
      where       nr_seq_lote_mensal = current_setting('tax_dmed_ir_pck.batch_monthly_sequence')::tax_dmed_lote_mensal.nr_sequencia%type;
      commit;

      for i in current_setting('tax_dmed_ir_pck.establishment_vector')::establishment_type.first .. current_setting('tax_dmed_ir_pck.establishment_vector')::establishment_type.last loop	   
        PERFORM set_config('tax_dmed_ir_pck.reference_establishment', current_setting('tax_dmed_ir_pck.establishment_vector')::establishment_type[i].cd_estabelecimento, false);
        CALL tax_dmed_ir_pck.generate_provider_data();
        CALL tax_dmed_ir_pck.generate_operator_data();
      end loop;

      update  tax_dmed_lote_mensal
      set     dt_geracao = clock_timestamp()
      where   nr_sequencia = batch_monthly_sequence_p;
      commit;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
