CREATE OR REPLACE PROCEDURE tax_dmed_ir_pck.generate_file_data (sequence_p tax_dmed_controle.nr_sequencia%type, generation_user_p tax_dmed_controle.nm_usuario%type) AS $body$
BEGIN
      PERFORM set_config('tax_dmed_ir_pck.file_control_sequence', sequence_p, false);
      PERFORM set_config('tax_dmed_ir_pck.generation_user', generation_user_p, false);

      CALL tax_dmed_ir_pck.load_control_data();
      CALL tax_dmed_ir_pck.load_data_establishment();

      delete from tax_dmed_arquivo where nm_usuario = generation_user_p;

      CALL tax_dmed_ir_pck.load_file_header();
      CALL tax_dmed_ir_pck.load_responsible_technician();
      CALL tax_dmed_ir_pck.load_responsible_establishment();

      if (current_setting('tax_dmed_ir_pck.data_establishment_vector')::data_establishment_reference[1].ie_commercial_ops = 2) or (current_setting('tax_dmed_ir_pck.data_establishment_vector')::data_establishment_reference[1].ie_commercial_ops = 3) then
          CALL tax_dmed_ir_pck.load_data_responsible('N', 'OPPAS', 'TOP', 'DTOP');
      end if;

      if (current_setting('tax_dmed_ir_pck.data_establishment_vector')::data_establishment_reference[1].ie_commercial_ops = 1) or (current_setting('tax_dmed_ir_pck.data_establishment_vector')::data_establishment_reference[1].ie_commercial_ops = 3) then
          CALL tax_dmed_ir_pck.load_data_responsible('S', 'PSS', 'RPPSS', 'BRPPSS');
      end if;

      CALL tax_dmed_ir_pck.load_file_footer();
      CALL tax_dmed_ir_pck.write_file_data_table();

      update  tax_dmed_controle
      set     dt_geracao = clock_timestamp()
      where   nr_sequencia = sequence_p;
      commit;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
