CREATE OR REPLACE PROCEDURE tax_dmed_ir_pck.generate_irpf (nr_seq_dmed_irpf_p tax_dmed_irpf.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_lote_w		tax_dmed_lote.nr_sequencia%type;

C01 CURSOR(	nr_seq_lote_pc		tax_dmed_lote.nr_sequencia%type) FOR
    SELECT sum(a.vl_pago) vl_pago,
           sum(a.vl_reembolso) vl_reembolso,
	   a.cd_responsavel_pagamento cd_pagador,
	   a.nr_contrato nr_contrato
    from   tax_dmed_dados_gerais_v a
    where  nr_seq_lote = nr_seq_lote_pc
    group by
	    a.cd_responsavel_pagamento,
            a.nr_contrato;

BEGIN

select	b.nr_seq_lote
into STRICT	nr_seq_lote_w
from	tax_dmed_irpf a,
	tax_dmed_controle b
where	b.nr_sequencia	= a.nr_seq_controle
and	a.nr_sequencia	= nr_seq_dmed_irpf_p;

for c01_w in C01(nr_seq_lote_w) loop
	begin
	insert	into	tax_dmed_irpf_payer(	nr_sequencia, nr_seq_dmed_irpf, cd_pagador,
			dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
			nm_usuario_nrec, nr_contrato, vl_pago,
			vl_reembolso)
		values (	nextval('tax_dmed_irpf_payer_seq'), nr_seq_dmed_irpf_p, c01_w.cd_pagador,
			clock_timestamp(), nm_usuario_p, clock_timestamp(),
			nm_usuario_p, c01_w.nr_contrato, c01_w.vl_pago,
			c01_w.vl_reembolso);
	end;
end loop;

update 	tax_dmed_irpf
set	dt_geracao = clock_timestamp(),
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where	nr_sequencia = nr_seq_dmed_irpf_p;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
