CREATE OR REPLACE FUNCTION tax_dmed_ir_pck.get_sequence_monthly_payment (monthly_item_type_p fis_dados_dmed.ie_tipo_item_mensalidade%type) RETURNS TAX_DMED_REGRA_TIPO_ITEM.NR_SEQUENCIA%TYPE AS $body$
DECLARE

    sequence_monthly_pay_w tax_dmed_regra_tipo_item.nr_sequencia%type;

BEGIN
    select coalesce(max(w.nr_sequencia), 0)    
    into STRICT   sequence_monthly_pay_w                              
    from   tax_dmed_regra_tipo_item w                                     
    where  w.nr_tipo_item = monthly_item_type_p                  
    and    w.nr_seq_lote = current_setting('tax_dmed_ir_pck.batch_sequence')::tax_dmed_lote.nr_sequencia%type;

    return sequence_monthly_pay_w;
  END;

$body$
