CREATE OR REPLACE PROCEDURE tax_dmed_ir_pck.undo_file_generation (sequence_p tax_dmed_controle.nr_sequencia%type) AS $body$
DECLARE

      qt_irpf_gerado_w integer;

  
BEGIN
      select  count(1)
      into STRICT    qt_irpf_gerado_w
      from    tax_dmed_irpf
      where   nr_seq_controle = sequence_p
      and     (dt_geracao IS NOT NULL AND dt_geracao::text <> '');

      if (qt_irpf_gerado_w > 0) then
	   CALL wheb_mensagem_pck.exibir_mensagem_abort(1131593);
      end if;

      update  tax_dmed_controle
      set     dt_geracao  = NULL
      where   nr_sequencia = sequence_p;
      commit;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
