SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE tax_dmed_ir_pck.undo_monthly_batch (batch_monthly_sequence_p tax_dmed_lote_mensal.nr_sequencia%type) AS $body$
DECLARE

      qt_arquivo_gerado_w integer;

BEGIN
      select  count(1)
      into STRICT    qt_arquivo_gerado_w
      from    tax_dmed_lote a,
              tax_dmed_lote_mensal b,
	            tax_dmed_controle c
      where   (c.dt_geracao IS NOT NULL AND c.dt_geracao::text <> '')
      and     a.nr_sequencia = c.nr_seq_lote
      and     a.nr_sequencia = b.nr_seq_lote
      and     b.nr_sequencia = batch_monthly_sequence_p;

      if (qt_arquivo_gerado_w > 0) then
        CALL wheb_mensagem_pck.exibir_mensagem_abort(1131592);
      end if;
  
      delete from tax_dmed_dados_gerais 
      where nr_seq_lote_mensal = batch_monthly_sequence_p;
      commit;

      update  tax_dmed_lote_mensal
      set     dt_geracao  = NULL
      where   nr_sequencia = batch_monthly_sequence_p;
      commit;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
