CREATE OR REPLACE FUNCTION tax_dmed_ir_pck.vector_to_str ( ie_option text, additional_index bigint default 0) RETURNS varchar AS $body$
DECLARE

    vector_rule_w generic_type;
    vector_rule_log_w varchar(1000):='';
    aux_w varchar(5);

BEGIN
    vector_rule_w := tax_dmed_ir_pck.get_vector(ie_option,additional_index);
    if (vector_rule_w is not empty) then
      vector_rule_log_w := '(';
      for i in vector_rule_w.first .. vector_rule_w.last loop	
        aux_w := chr(39) ||  vector_rule_w(i) || chr(39);
        if (i <> 1) then
          vector_rule_log_w :=  vector_rule_log_w || ' ,';
        end if;
        vector_rule_log_w :=  vector_rule_log_w || aux_w;
      end loop;
      vector_rule_log_w := vector_rule_log_w || ')';
    end if;

    return vector_rule_log_w;
  END;

$body$
