CREATE OR REPLACE FUNCTION tax_invoice_data_pck.next_city_code (environment_p bigint) RETURNS bigint AS $body$
DECLARE

    city_code_w     bigint;

BEGIN
    select  max((vl_dominio)::numeric ) + 1
    into STRICT    city_code_w
    from    valor_dominio
    where   ((environment_p = 1) and (cd_dominio = 4195) 
    or (environment_p = 2) and (cd_dominio = 4194));

    return city_code_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
