CREATE OR REPLACE PROCEDURE tax_parallel_procedure_pck.sleep (in_time_p bigint) AS $body$
DECLARE

        current_date_w timestamp;

BEGIN
        select  clock_timestamp()
        into STRICT    current_date_w
;

        loop
            exit when current_date_w + (in_time_p * (1/86400)) <= clock_timestamp();
        end loop;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
