CREATE OR REPLACE FUNCTION tax_reinf_data_pck.changedatetodefault ( date_p text ) RETURNS timestamp AS $body$
DECLARE

   date_w       timestamp;
   date_aux_w   varchar(255);
   index_aux_w  bigint;

BEGIN
    begin
      date_aux_w := date_p;
      index_aux_w := position('.' in date_aux_w);

      if index_aux_w > 0 then
        date_aux_w := substr(date_aux_w, 1, index_aux_w - 1);
      end if;

      date_w := replace(date_aux_w, 'T', ' ');
      exception when others then
      begin
        date_w := to_date(replace(date_aux_w, 'T', ' '), 'yyyy-mm-dd hh24:mi:ss');
        exception when others then
          begin
            date_w := to_date(replace(date_aux_w, 'T', ' '), 'dd-mm-yyyy hh24:mi:ss');
            exception when others then
              CALL wheb_mensagem_pck.exibir_mensagem_abort(186114,'DT_EMISSAO_NFE=' || date_aux_w);
          end;
      end;
    end;
    return date_w;
  END;

$body$
