SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION tax_reinf_data_pck.format_url (url_p text) RETURNS varchar AS $body$
DECLARE

    formatted_url_w   varchar(255);
    index_w           bigint;

BEGIN
    formatted_url_w := url_p;
    index_w := position('://' in formatted_url_w);

    if (index_w > 0) then
      formatted_url_w := substr(formatted_url_w, index_w + 3);
    end if;

    index_w := position('/' in formatted_url_w);

    if (index_w > 0) then
      formatted_url_w := substr(formatted_url_w, 0, index_w - 1);
    end if;

    return formatted_url_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
