SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION tax_reinf_data_pck.get_data_r2020 (batch_code_p text) RETURNS SETOF R2020_TABLE_W AS $body$
DECLARE

    r2020_data_w    r2020_record_w;

    c_2020 CURSOR FOR
      SELECT  tax_reinf_data_pck.get_event_id(subscription, rownum) identification,
              '1' emission,	
              '1' subscription_type, 
              substr(subscription, 1, 8) subscription, 
              supplier_registration, 
              environment_type,
              rectification,
              receipt,
              to_char(competence, 'rrrr-mm') competence,
              1 estab_subscription_type,
              subscription establishment_subscription,
              1 works_indicator,
              gross_value,
              base_value_retained,
              value_retained,
              value_retained_addit,
              value_non_retained
      from (  
        SELECT  e.cd_cgc subscription,
                f.ie_ambiente environment_type,
                f.ie_retificacao rectification,
                f.nr_recibo	receipt,
                f.dt_competencia competence,
                n.cd_cnpj supplier_registration,
                sum(n.vl_bruto_nf) gross_value, 
                sum(n.vl_base_trib_inss) base_value_retained, 
                sum(n.vl_retencao_inss) value_retained, 
                sum(vl_servicos_15) + sum(vl_servicos_20) + sum(vl_servicos_25) value_retained_addit,
                sum(n.vl_n_retencao_inss) value_non_retained
        from    estabelecimento e,
                fis_reinf_r2020 f,
                fis_reinf_notas_r2020 n
        where   e.cd_estabelecimento = f.cd_estabelecimento
        and	    n.nr_seq_superior = f.nr_sequencia
        and	    coalesce(n.dt_transmissao::text, '') = ''
        and	    coalesce(n.ie_status::text, '') = ''
        and     f.nr_sequencia = batch_code_p
        group by  f.ie_ambiente, 
                  e.cd_cgc,
                  f.ie_retificacao, 
                  f.nr_recibo, 
                  f.dt_competencia, 
                  n.cd_cnpj 
      ) alias12;

BEGIN
    for r2020_row in c_2020 loop
      begin
        r2020_data_w.identification                 := r2020_row.identification;
        r2020_data_w.emission                       := r2020_row.emission;
        r2020_data_w.subscription_type              := r2020_row.subscription_type;
        r2020_data_w.subscription                   := r2020_row.subscription;
        r2020_data_w.supplier_registration          := r2020_row.supplier_registration;
        r2020_data_w.environment_type               := r2020_row.environment_type;
        r2020_data_w.rectification                  := r2020_row.rectification;
        r2020_data_w.receipt                        := r2020_row.receipt;
        r2020_data_w.competence                     := r2020_row.competence;
        r2020_data_w.estab_subscription_type        := r2020_row.estab_subscription_type;
        r2020_data_w.establishment_subscription     := r2020_row.establishment_subscription;
        r2020_data_w.works_indicator                := r2020_row.works_indicator;
        r2020_data_w.gross_value                    := r2020_row.gross_value;
        r2020_data_w.base_value_retained            := r2020_row.base_value_retained;
        r2020_data_w.value_retained                 := r2020_row.value_retained;
        r2020_data_w.value_retained_addit           := r2020_row.value_retained_addit;
        r2020_data_w.value_non_retained             := r2020_row.value_non_retained;
        RETURN NEXT r2020_data_w;
      end;
    end loop;
    return;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
