CREATE OR REPLACE FUNCTION telematic_json_pck.generate_uid () RETURNS varchar AS $body$
DECLARE


uid_w varchar(128);


BEGIN
	SELECT REGEXP_REPLACE(RAWTOHEX(uuid_generate_v4()), '([A-F0-9]{8})([A-F0-9]{4})([A-F0-9]{4})([A-F0-9]{4})([A-F0-9]{12})', '\1-\2-\3-\4-\5')
	INTO STRICT uid_w
	;

	RETURN uid_w;
END;

